$(function () {
  $(".sidebarMenuScroll").overlayScrollbars({
    scrollbars: {
      visibility: "auto",
      autoHide: "scroll",
      autoHideDelay: 200,
      dragScrolling: true,
      clickScrolling: false,
      touchSupport: true,
      snapHandle: false,
    },
  });
});

